import React, { useState, useEffect } from "react";
import axios from "axios";
import { Modal, Button, Alert, ListGroup } from "react-bootstrap";
import loadingSpinner from "../assets/loading.gif"; // Ensure you have a loading spinner

function Notes() {
  const [notes, setNotes] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isEditing, setIsEditing] = useState(false);
  const [currentNote, setCurrentNote] = useState(null);
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [showSuccessMessage, setShowSuccessMessage] = useState(false);
  const [errors, setErrors] = useState({});

  useEffect(() => {
    fetchNotes();
  }, []);

  // Function to fetch notes for the logged-in user
  const fetchNotes = async () => {
    setIsLoading(true);
    try {
      const response = await axios.get("/notes/", {
        headers: {
          Authorization: `Token ${localStorage.getItem('token')}`,
        },
      });
      setNotes(response.data);
    } catch (error) {
      console.error("Error fetching notes:", error);
      setErrors({ non_field_errors: ["Failed to load notes."] });
    } finally {
      setIsLoading(false);
    }
  };

  // Function to handle adding or editing a note
  const handleSaveNote = async () => {
    setIsLoading(true);
    const noteData = {
      title,
      content,
    };

    try {
      if (isEditing) {
        // Update existing note
        const response = await axios.patch(`/notes/${currentNote.id}/`, noteData, {
          headers: {
            Authorization: `Token ${localStorage.getItem('token')}`,
          },
        });
        // Update the notes state with the edited note
        setNotes((prevNotes) =>
          prevNotes.map((note) =>
            note.id === response.data.id ? response.data : note
          )
        );
      } else {
        // Create new note
        const response = await axios.post("/notes/", noteData, {
          headers: {
            Authorization: `Token ${localStorage.getItem('token')}`,
          },
        });
        setNotes((prevNotes) => [...prevNotes, response.data]);
      }
      setShowSuccessMessage(true);
      setTimeout(() => setShowSuccessMessage(false), 3000);
      handleCancelEdit();
    } catch (error) {
      console.error("Error saving note:", error);
      setErrors(error.response?.data || { non_field_errors: ["Failed to save note."] });
    } finally {
      setIsLoading(false);
    }
  };

  // Function to handle note deletion
  const handleDeleteNote = async (noteId) => {
    if (window.confirm("Are you sure you want to delete this note?")) {
      setIsLoading(true);
      try {
        await axios.delete(`/notes/${noteId}/`, {
          headers: {
            Authorization: `Token ${localStorage.getItem('token')}`,
          },
        });
        // Remove the deleted note from the state
        setNotes((prevNotes) => prevNotes.filter((note) => note.id !== noteId));
      } catch (error) {
        console.error("Error deleting note:", error);
      } finally {
        setIsLoading(false);
      }
    }
  };

  const handleCancelEdit = () => {
    setIsEditing(false);
    setCurrentNote(null);
    setTitle("");
    setContent("");
  };

  // Loading screen
  if (isLoading) {
    return (
      <div style={{ position: 'fixed', top: 0, left: 0, width: '100%', height: '100%', backgroundColor: 'rgba(255, 255, 255, 0.8)', display: 'flex', justifyContent: 'center', alignItems: 'center', zIndex: 9999 }}>
        <img src={loadingSpinner} alt="Loading..." style={{ width: '75px', height: '75px' }} />
      </div>
    );
  }

  return (
    <div>
      {showSuccessMessage && (
        <Alert variant="success" style={{ position: 'fixed', top: '100px', right: '20px', zIndex: 1000 }}>
          Note saved successfully!
        </Alert>
      )}
      <Button variant="dark" onClick={() => setIsEditing(true)}>Add Note</Button>

      <ListGroup className="mt-3">
        {notes.map((note) => (
          <ListGroup.Item key={note.id} className="d-flex justify-content-between align-items-center">
            <div>
              <strong>{note.title}</strong>
              <p>{note.content}</p>
            </div>
            <div>
              <Button variant="primary" onClick={() => {
                setCurrentNote(note);
                setTitle(note.title);
                setContent(note.content);
                setIsEditing(true);
              }}>Edit</Button>
              <Button variant="danger" onClick={() => handleDeleteNote(note.id)} className="ms-2">Delete</Button>
            </div>
          </ListGroup.Item>
        ))}
      </ListGroup>

      {/* Modal for Adding/Editing Notes */}
      <Modal show={isEditing} onHide={handleCancelEdit} centered>
        <Modal.Header closeButton>
          <Modal.Title>{currentNote ? "Edit Note" : "Add Note"}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div>
            <label htmlFor="noteTitle">Title</label>
            <input
              type="text"
              id="noteTitle"
              className="form-control"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
          </div>
          <div className="mt-3">
            <label htmlFor="noteContent">Content</label>
            <textarea
              id="noteContent"
              className="form-control"
              value={content}
              onChange={(e) => setContent(e.target.value)}
            />
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCancelEdit}>
            Cancel
          </Button>
          <Button variant="primary" onClick={handleSaveNote}>
            {currentNote ? "Save Changes" : "Add Note"}
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default Notes;
