import React, { useState } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import styles from '../styles/Home.module.css';

function Comments({ postId, fetchCommentsForPost }) {
  const [comment, setComment] = useState('');
  const [errors, setErrors] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleCommentChange = (e) => {
    setComment(e.target.value);
  };

  const handleCommentSubmit = async () => {
    if (comment.trim() === '') {
      setErrors('Comment cannot be empty');
      return;
    }

    if (!postId) {
      setErrors('Post ID is invalid');
      return;
    }

    setLoading(true);
    setErrors(null);  // Clear any previous errors

    const formData = new FormData();
    formData.append('housepost', postId);
    formData.append('comment', comment);

    const csrfToken = Cookies.get('csrftoken');

    try {
      await axios.post('http://127.0.0.1:8000/housepostcomments/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'X-CSRFToken': csrfToken,
          Authorization: `Token ${localStorage.getItem('token')}`,
        },
      });

      setComment(''); // Clear the comment input
      fetchCommentsForPost(postId); // Refresh comments after adding a new one
    } catch (error) {
      console.error('Error submitting comment:', error.response ? error.response.data : error.message);
      setErrors(error.response?.data || { non_field_errors: ['Failed to submit the comment. Please try again.'] });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="input-group mt-3">
      <input
        type="text"
        className="form-control"
        placeholder="Add a comment..."
        aria-label="Add a comment"
        value={comment}
        onChange={handleCommentChange}
        disabled={loading}
      />
      <button
        className={`btn ${styles.SubmitButton}`}
        type="button"
        onClick={handleCommentSubmit}
        disabled={loading}
      >
        {loading ? 'Submitting...' : 'Comment'}
        {!loading && <span className="material-symbols-outlined">send</span>}
      </button>
      {errors && <div className="text-danger mt-2">{errors.non_field_errors}</div>}
    </div>
  );
}

export default Comments;
