import React, { useState } from 'react';
import { axiosRes } from '../api/axiosDefaults'; // Ensure this is configured similarly to your school example
import styles from '../styles/Home.module.css';

function Comments({ postId, fetchCommentsForPost }) {
  const [comment, setComment] = useState('');
  const [errors, setErrors] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleCommentChange = (e) => {
    setComment(e.target.value);
  };

  const handleCommentSubmit = async (event) => {
    event.preventDefault();

    if (comment.trim() === '') {
      setErrors('Comment cannot be empty');
      return;
    }

    setLoading(true);
    setErrors(null);  // Clear any previous errors

    try {
      await axiosRes.post('/housepostcomments/', {
        housepost: postId,
        comment: comment.trim(),
      });

      setComment(''); // Clear the comment input
      fetchCommentsForPost(postId); // Refresh comments after adding a new one
    } catch (error) {
      console.error('Error submitting comment:', error.response ? error.response.data : error.message);
      setErrors('Failed to submit the comment. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Form onSubmit={handleCommentSubmit}>
      <Form.Group className="pr-1">
        <Form.Control
          className={styles.Form}
          as="textarea"
          value={comment}
          onChange={handleCommentChange}
          rows={2}
          placeholder="Add a comment..."
          disabled={loading}
        />
      </Form.Group>
      <div className="text-right">
        <button
          className={styles.Button}
          type="submit"
          disabled={loading || comment.trim() === ''}
        >
          {loading ? 'Submitting...' : 'Comment'}
        </button>
        {errors && (
          <div className="text-danger mt-2">
            {errors}
          </div>
        )}
      </div>
    </Form>
  );
}

export default Comments;
