import React, { useState } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import styles from '../styles/Home.module.css';

function Comments({ postId, fetchCommentsForPost }) {
  const [comment, setComment] = useState('');
  const [errors, setErrors] = useState(null);

  const handleCommentChange = (e) => {
    setComment(e.target.value);
  };

  const handleCommentSubmit = async () => {
    if (comment.trim() === '') {
      setErrors('Comment cannot be empty');
      return;
    }

    if (!postId) {
      setErrors('Post ID is invalid');
      return;
    }

    const token = localStorage.getItem('token'); // Retrieve the auth token from localStorage
    const csrfToken = Cookies.get('csrftoken'); // Get CSRF token from cookies

    if (!token) {
      setErrors('Authentication token not found. Please log in.');
      return;
    }

    const newComment = {
      housepost: postId,
      comment: comment,
    };

    try {
      const response = await axios.post('http://127.0.0.1:8000/housepostcomments/', newComment, {
        headers: {
          Authorization: `Token ${token}`, // Include the auth token in the request headers
          'X-CSRFToken': csrfToken, // Include the CSRF token in the request headers
          'Content-Type': 'application/json', // Specify that the request body is in JSON format
        },
        data: JSON.stringify(newComment), // Convert the newComment object to a JSON string
      });

      setComment(''); // Clear the comment input
      fetchCommentsForPost(postId); // Refresh comments after adding a new one
      setErrors(null); // Clear any previous errors
    } catch (error) {
      console.error('Error submitting comment:', error.response ? error.response.data : error.message);
      setErrors('Failed to submit the comment. Please try again.');
    }
  };

  return (
    <div className="input-group mt-3">
      <input
        type="text"
        className="form-control"
        placeholder="Add a comment..."
        aria-label="Add a comment"
        value={comment}
        onChange={handleCommentChange}
      />
      <button className={`btn ${styles.SubmitButton}`} type="button" onClick={handleCommentSubmit}>
        Comment
        <span className="material-symbols-outlined">send</span>
      </button>
      {errors && <div className="text-danger mt-2">{errors}</div>}
    </div>
  );
}

export default Comments;
