import React, { useState, useRef, useEffect } from 'react';
import { Form, Button, Image, Container, Row, Col, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { axiosReq } from '../api/axiosDefaults';

function Upload() {
  const [postData, setPostData] = useState({
    title: '',
    description: '',
    image: '',
  });
  const [errors, setErrors] = useState({});
  const { title, description, image } = postData;
  const navigate = useNavigate();
  const imageInput = useRef(null);

  const handleChange = (event) => {
    setPostData({
      ...postData,
      [event.target.name]: event.target.value,
    });
  };

  const handleChangeImage = (event) => {
    if (event.target.files.length) {
      setPostData({
        ...postData,
        image: event.target.files[0], // Store the actual file, not the URL
      });
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append('house_title', title); // Ensure the field names match the backend expectations
    formData.append('description', description);
    if (image) {
      formData.append('house_image', image); // Use the actual file here
    }

    try {
      const { data } = await axiosReq.post('/houseposts/', formData);
      navigate(`/houseposts/${data.id}`); // Redirect to the newly created post
    } catch (err) {
      setErrors(err.response?.data || {});
    }
  };

  useEffect(() => {
    return () => {
      if (image) URL.revokeObjectURL(image); // Clean up the image URL if it exists
    };
  }, [image]);

  return (
    <Container className="mt-5">
      <div className="card p-4" style={{ borderRadius: '10px' }}>
        <Form onSubmit={handleSubmit}>
          <Row>
            <Col xs={12} md={4} className="d-flex flex-column align-items-center mb-4 mb-md-0">
              <Form.Group className="text-center">
                {image ? (
                  <>
                    <figure>
                      <Image
                        src={URL.createObjectURL(image)} // Preview the selected image
                        rounded
                        style={{ width: '100%', height: '200px', objectFit: 'cover' }}
                      />
                    </figure>
                    <Form.Label
                      className="btn btn-primary mt-2"
                      onClick={() => imageInput.current.click()}
                    >
                      Change the image
                    </Form.Label>
                  </>
                ) : (
                  <Form.Label
                    className="d-flex flex-column align-items-center"
                    htmlFor="image-upload"
                    style={{
                      width: '100%',
                      height: '200px',
                      border: '2px dashed #ccc',
                      borderRadius: '10px',
                      backgroundColor: '#f8f9fa',
                      justifyContent: 'center',
                      display: 'flex',
                      cursor: 'pointer',
                    }}
                  >
                    <span className="material-symbols-outlined" style={{ fontSize: '48px', color: '#6c757d' }}>
                      add_photo_alternate
                    </span>
                    <p className="mt-2 text-center" style={{ color: '#6c757d' }}>
                      Click or tap to upload image of dream house
                    </p>
                  </Form.Label>
                )}
                <Form.Control
                  type="file"
                  id="image-upload"
                  accept="image/*"
                  onChange={handleChangeImage}
                  style={{ display: 'none' }}
                  ref={imageInput}
                />
                {errors.image && <Alert variant="danger">{errors.image}</Alert>}
              </Form.Group>
            </Col>

            <Col xs={12} md={8}>
              <Form.Group>
                <Form.Label htmlFor="title">Title</Form.Label>
                <Form.Control
                  type="text"
                  id="title"
                  name="title"
                  value={title}
                  onChange={handleChange}
                  placeholder="Enter a title"
                  required
                  autoComplete="off"
                />
                {errors.title && <Alert variant="danger">{errors.title}</Alert>}
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label htmlFor="description">Description</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={3}
                  id="description"
                  name="description"
                  value={description}
                  onChange={handleChange}
                  placeholder="Write a description"
                  required
                  autoComplete="off"
                />
                {errors.description && <Alert variant="danger">{errors.description}</Alert>}
              </Form.Group>

              <Button variant="dark" type="submit">
                Add Housepost
              </Button>
            </Col>
          </Row>
        </Form>
      </div>
    </Container>
  );
}

export default Upload;
