import React, { useState } from 'react';
import axios from 'axios';
import heartNotLiked from '../assets/househeart-not-liked.png';
import heartLiked from '../assets/househeart-liked.png';

const HouseHearts = ({ postId, currentHeartCount, fetchHousePosts }) => {
    const [isLiked, setIsLiked] = useState(false);
    const [heartsCount, setHeartsCount] = useState(currentHeartCount);

    const loggedInUser = JSON.parse(localStorage.getItem('user'))?.username;

    const handleLike = () => {
      if (isLiked) {
          return;
      }

      const token = localStorage.getItem('token');  // Assuming your token is stored in localStorage

      axios.post('http://127.0.0.1:8000/househearts/', {
          housepost: postId,
          user: loggedInUser,
      }, {
          headers: {
              'Authorization': `Bearer ${token}`
          }
      })
      .then(response => {
          setIsLiked(true);
          setHeartsCount(heartsCount + 1);
          fetchHousePosts();
      })
      .catch(error => {
          if (error.response && error.response.status === 400) {
              console.error("You cannot like your own post.");
          } else if (error.response && error.response.status === 401) {
              console.error("You are not authorized to like this post. Please log in.");
          } else {
              console.error('Error liking the post:', error);
          }
      });
  };


    return (
        <button className="btn btn-link p-0" type="button" onClick={handleLike}>
            <img
                src={isLiked ? heartLiked : heartNotLiked}
                alt="Like Button"
                style={{ width: '28.8px', height: '28.8px' }}
            />
            <span>{heartsCount}</span>
        </button>
    );
};

export default HouseHearts;
