import React, { useState } from 'react';
import axios from 'axios';
import styles from '../styles/Home.module.css';

function Comments({ postId, fetchCommentsForPost }) {
  const [comment, setComment] = useState('');

  const handleCommentChange = (e) => {
    setComment(e.target.value);
  };

  const handleCommentSubmit = () => {
    if (comment.trim() === '') return;

    const token = localStorage.getItem('token'); // Assuming the token is stored in localStorage
    const csrfToken = document.querySelector('[name=csrfmiddlewaretoken]').value; // Assuming you have a CSRF token in your page

    const newComment = {
      housepost: postId,
      comment: comment,
    };

    axios.post('http://127.0.0.1:8000/housepostcomments/', newComment, {
      headers: {
        Authorization: `Bearer ${token}`, // Include the token in the request headers
        'X-CSRFToken': csrfToken, // Include CSRF token if needed
      },
    })
      .then(response => {
        setComment('');
        fetchCommentsForPost(postId);  // Refresh comments after adding a new one
      })
      .catch(error => {
        console.error('Error submitting comment:', error);
      });
  };

  return (
    <div className="input-group mt-3">
      <input
        type="text"
        className="form-control"
        placeholder="Add a comment..."
        aria-label="Add a comment"
        value={comment}
        onChange={handleCommentChange}
      />
      <button className={`btn ${styles.SubmitButton}`} type="button" onClick={handleCommentSubmit}>
        Comment
        <span className="material-symbols-outlined">send</span>
      </button>
    </div>
  );
}

export default Comments;
