import React, { useState, useEffect } from "react";
import axios from "axios";
import Cookies from "js-cookie";
import styles from "../styles/Profile.module.css";
import loadingGif from "../assets/loading.gif"; // Update with your actual path
import { useSetCurrentUser } from "../context/CurrentUserContext"; // Adjust the path as needed

function Profile() {
  const [userProfile, setUserProfile] = useState(null);
  const [displayName, setDisplayName] = useState("");
  const [bio, setBio] = useState("");
  const [profilePicture, setProfilePicture] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const [errors, setErrors] = useState({});
  const setCurrentUser = useSetCurrentUser();

  useEffect(() => {
    // Function to get current user profile
    const fetchUserProfile = async () => {
      try {
        // Fetch the current user details
        const userResponse = await axios.get("/dj-rest-auth/user/", {
          headers: {
            Authorization: `Token ${localStorage.getItem('token')}`,
          },
        });

        const userId = userResponse.data.pk; // Assuming pk is the user ID
        setCurrentUser(userResponse.data);

        // Fetch the profile data using the user ID
        const response = await axios.get(`/userprofiles/${userId}/`, {
          headers: {
            Authorization: `Token ${localStorage.getItem('token')}`,
          },
        });

        const data = response.data;
        setUserProfile(data);
        setDisplayName(data.display_name);
        setBio(data.bio);
        setProfilePicture(
          data.profile_picture ||
            "https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_1280.png"
        );
        setIsLoading(false);
      } catch (error) {
        console.error("Failed to fetch user profile:", error);
        setErrors({ non_field_errors: ["Failed to load profile data."] });
        setIsLoading(false);
      }
    };

    fetchUserProfile();
  }, [setCurrentUser]);

  const handleSaveProfile = async () => {
    const updatedProfile = {
      display_name: displayName,
      bio: bio,
    };

    setIsLoading(true);

    try {
      const response = await axios.patch(`/userprofiles/${userProfile.id}/`, updatedProfile, {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Token ${localStorage.getItem('token')}`,
        },
      });

      setUserProfile(response.data);
      alert("Profile updated successfully!");
    } catch (error) {
      console.error("Error updating profile:", error);
      setErrors(error.response?.data || { non_field_errors: ["Failed to update profile."] });
    } finally {
      setIsLoading(false);
    }
  };

  if (isLoading) {
    return (
      <div className={styles.loadingOverlay}>
        <img src={loadingGif} alt="Loading..." className={styles.loadingSpinner} />
      </div>
    );
  }

  return (
    <div className={styles.profileCard}>
      <img
        src={profilePicture}
        alt="Profile"
        className={styles.profileImage}
      />
      <div className={styles.profileDetails}>
        <h1 className={styles.profileName}>
          <input
            type="text"
            value={displayName}
            onChange={(e) => setDisplayName(e.target.value)}
            placeholder="Enter your display name"
          />
        </h1>
        <p className={styles.profileBio}>
          <textarea
            value={bio}
            onChange={(e) => setBio(e.target.value)}
            placeholder="Enter your bio"
          />
        </p>
        <button onClick={handleSaveProfile}>Save</button>
        {errors.non_field_errors?.map((message, idx) => (
          <div key={idx} className="alert alert-danger mt-2">{message}</div>
        ))}
      </div>
    </div>
  );
}

export default Profile;
